document.addEventListener('DOMContentLoaded',function(){const T="Buone Feste a tutti i nostri clienti",D=4000,A=1200;const s=document.createElement('style');s.textContent=`@import url('https://fonts.googleapis.com/css2?family=Great+Vibes&display=swap');.christmas-message-container{position:fixed;top:50%;left:50%;transform:translate(-50%,-50%);color:#e00000;font-family:'Great Vibes',cursive;font-size:6vw;font-weight:normal;text-align:center;z-index:10000;opacity:0;pointer-events:none;white-space:nowrap;animation-fill-mode:forwards;text-shadow:0 0 10px rgba(0,0,0,.7),0 0 20px rgba(255,255,255,.5),0 0 30px rgba(255,255,255,.3)}@keyframes fadeInZoom{0%{opacity:0;transform:translate(-50%,-50%) scale(.7)}100%{opacity:1;transform:translate(-50%,-50%) scale(1)}}@keyframes fadeOutZoom{0%{opacity:1;transform:translate(-50%,-50%) scale(1)}100%{opacity:0;transform:translate(-50%,-50%) scale(.7)}}`;document.head.appendChild(s);const m=document.createElement('div');m.classList.add('christmas-message-container');m.textContent=T;document.body.appendChild(m);m.style.animation=`fadeInZoom ${A/1000}s ease-out forwards`;setTimeout(()=>{m.style.animation=`fadeOutZoom ${A/1000}s ease-in forwards`;setTimeout(()=>{m.remove()},A)},D)});
