document.addEventListener('DOMContentLoaded',function(){const N=50,S=5,X=15,D=10,A=25;function R(a,b){return Math.random()*(b-a)+a}const s=document.createElement('style');s.textContent=`.snow-container-injected{position:fixed;top:0;left:0;width:100vw;height:100vh;pointer-events:none;z-index:9999;overflow:hidden}.snowflake-injected{position:absolute;background:white;border-radius:50%;opacity:.8;filter:drop-shadow(0 0 2px rgba(0,0,0,.3));animation:fall linear infinite}@keyframes fall{0%{transform:translateY(-10vh)}100%{transform:translateY(100vh)}}`;document.head.appendChild(s);const c=document.createElement('div');c.classList.add('snow-container-injected');document.body.appendChild(c);for(let i=0;i<N;i++){const f=document.createElement('div'),z=R(S,X);f.classList.add('snowflake-injected');f.style.left=R(0,100)+'vw';f.style.width=z+'px';f.style.height=z+'px';f.style.animationDuration=R(D,A)+'s';f.style.animationDelay='-'+R(0,A)+'s';f.style.opacity=R(.5,1);c.appendChild(f)}});
